}

.SeriesIndexPoster\/title\/263Pq,
.SeriesIndexPoster\/nextAiring\/1FZy6 {
    background: transparent;
    font-size: 10px;
    text-transform: uppercase;
    color: #555;
    padding: 0px 5px 0px;
}


.SeriesIndexPoster\/content\/uRtdS > div:nth-child(3) {
    margin-top: 5px;
}


.SeriesIndexPoster\/nextAiring\/1FZy6 {
    margin-top: 2px;
    color: #fff;
    background-color: orange;
    border-radius: 6px;
}


.ProgressBar\/container\/14z4A {
    margin-top: 4px;
}


.SeriesIndexPoster\/controls\/prEIO {
    bottom: -10px;
    opacity: 0;
    background-color: rgba(255, 255, 255) !important;
    box-shadow: 0 0 6px rgba(0, 0, 0, 1);
    transition: all 100ms ease-out;
}

.SeriesIndexPoster\/content\/uRtdS:hover .SeriesIndexPoster\/controls\/prEIO {
    bottom: 10px;
    opacity: 1.0;
    transition: all 195ms ease-in;
}


.SeriesIndexPoster\/content\/uRtdS:hover {
    box-shadow: none;
}

.SeriesIndexPoster\/content\/uRtdS:hover img {
    width: 102% !important;
    height: 102% !important;
    top: -1%;
    left: -1%;
    transition: all 195ms ease-in;
    opacity: 1.0;
    box-shadow: 0 0 6px rgba(0, 0, 0, 1);
    filter: none;
}


.SeriesIndexPoster\/content\/uRtdS img {
    position: relative;
    top: 0;
    left: 0;
    transition: all 195ms ease-out;
}


/* Series has ended */
.SeriesIndexPoster\/ended\/3RrLo {
    border: none;
}


/* Series has ended */
.SeriesIndexPoster\/ended\/3RrLo::after {
    position: absolute;
    top: 15px;
    right: -15px;
    content: 'ENDED';
    background: #c00;
    color: #FFF;
    font-size: 10px;
    letter-spacing: 3px;
    padding: 2px 5px 2px 8px;
    box-shadow: 0 5px 5px -3px rgba(0, 0, 0, 0.2);
    transform: rotate(90deg);
    transition: all 195ms ease-out;
}

.SeriesIndexPoster\/ended\/3RrLo + a img {
    filter: grayscale(100%);
    opacity: 0.5;
}


.SeriesIndexPoster\/content\/uRtdS:hover .SeriesIndexPoster\/ended\/3RrLo {
    opacity: 0;
    transition: all 195ms ease-in;
}

.SeriesIndexPoster\/content\/uRtdS:hover .SeriesIndexPoster\/ended\/3RrLo::after {
    transform: rotate(90deg);
    top: -40px;
    opacity: 0;
    transition: all 195ms ease-in;
}

